package Temperaturas;public class TemperaturasGlobais {    public static void main(String[] args) {        String[] cidades = {"Londres", "Madrid", "New York", "Buenos Aires", "Asunción", "São Paulo", "Lima",                "Santiago de Chile", "Lisboa", "Tokyo"};        int[][] temperaturas = {{-2, 33}, {-3, 32}, {-8, 27}, {4, 37}, {6, 42}, {5, 43}, {0, 39}, {-7, 26}, {-1, 31},                {-10, 35}};        int maiorTemperatura = 0;        int menorTemperatura = 0;        String cidadeQuente = "null";        String cidadeFria = "null";        for (int i = 0; i < temperaturas.length; i++) {            for (int j = 0; j < temperaturas[i].length; j++) {                if (menorTemperatura > temperaturas[i][j]) {                    menorTemperatura = temperaturas[i][j];                    cidadeFria = cidades[i];                }                if (maiorTemperatura < temperaturas[i][j]) {                    maiorTemperatura = temperaturas[i][j];                    cidadeQuente = cidades[i];                }            }        }        System.out.println("Cidade mais fria: " + menorTemperatura + " em " + cidadeFria + "\nCidade mais quente: " + maiorTemperatura + " em " + cidadeQuente);    }}